import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { detailsUser } from "../actions/userActions";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";

export default function ProfileScreen() {
  const userSign = useSelector((state) => state.userSign);
  const { userInfo } = userSign;
  const dispatch = useDispatch();
  const userDetails = useSelector((state)=> state.userDetails);
  const {loading,error, user} = userDetails;
  useEffect(() => {
    dispatch(detailsUser(userInfo._id));
  }, [dispatch, userInfo._id]);
  const submitHandler=(e)=> {
      e.preventDefault();
      //dispatch update profile
  }
  return (
    <div>
      <form className="form" onSubmit={submitHandler}>
        <div>
          <h1>User Profile</h1>
        </div>
        {loading ? (
          <LoadingBox></LoadingBox>
        ) : error ? (
          <MessageBox></MessageBox>
        ) : (
          <>
            <div>
              <label htmlFor="name">Name</label>
              <input id="name" type="text" placeholder="Enter name" value={user.name} />
            </div>
            <div>
              <label htmlFor="email">Email</label>
              <input id="email" type="email" placeholder="Enter email" value={user.email} />
            </div>
            <div>
              <label htmlFor="password">Password</label>
              <input id="password" type="password" placeholder="Enter password"  />
            </div>
            <div>
              <label htmlFor="confirmPassword">confirm Password</label>
              <input id="confirmPassword" type="password" placeholder="Enter confirm password"  />
            </div>
            <div>
            <label />
            <button className="primary" type="submit">
              Update
            </button>
            </div>
          </>
        )}
      </form>
    </div>
  );
}

